// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract PriviNFTBuying {
  event OptionCreated(
    address owner,
    address nft,
    uint256 expiry,
    uint256 price,
    uint256 pct,
    uint256 optionID
  );

  event OptionCanceled(
    address owner,
    uint256 optionID
  );

  event OptionSold(
    address owner,
    address buyer,
    uint256 optionID
  );

  struct NFTOption {
    address owner;
    address nft;
    uint256 expiry;
    uint256 pct;
    uint256 optionID;
  }

  uint256 counter;

  mapping(uint256 => NFTOption) options;

  constructor() public {
  }

  function createOption(
    address nft,
    uint256 expiry,
    uint256 price,
    uint256 pct
  ) external returns (uint256 optionID) {
    optionID = counter;
    counter++;
    NFTOption memory option = NFTOption(msg.sender, nft, expiry, pct, optionID);
    options[optionID] = option;
    emit OptionCreated(msg.sender, nft, expiry, price, pct, optionID);
  }

  function cancelOption(
    uint256 optionID
  ) external returns (bool success) {

  }

  function buyOption(
    uint256 optionID
  ) external returns (bool success) {

  }

  function _getOption(uint256 optionID) private returns (NFTOption storage) {
    return options[optionID];
  }
}
